(2516, 'en', 'default', '7ec7508c587f87f5a01c3a078d6a6dab', 'WordPress Updates', NULL, 'LINE', NULL, 0, '', '1aeff3f3a99a487616cb48bba3497496'),
(2517, 'en', 'default', 'e45bfeb9fa95a202319d55f3ba06991b', 'On this screen, you can update to the latest version of WordPress, as well as update your themes, plugins, and translations from the WordPress.org repositories.', NULL, 'LINE', NULL, 0, '', 'e372d88371c5bbedffe3b8b4d1dc9d54'),
(2518, 'en', 'default', '413ea0a127a3a100b83ec95eacabc6c3', 'If an update is available, you&#8127;ll see a notification appear in the Toolbar and navigation menu.', NULL, 'LINE', NULL, 0, '', '27b11d9c8aed915251b6af3836623b77'),
(2519, 'en', 'default', 'f30629be5fa2dd68e817486c218122de', 'Keeping your site updated is important for security. It also makes the internet a safer place for you and your readers.', NULL, 'LINE', NULL, 0, '', 'd0eaf747d8cb15ad1a168eb6ba6e207c'),
(2520, 'en', 'default', '16fd34c3de2b02f60c7813d5965faecb', '<strong>WordPress</strong> &mdash; Updating your WordPress installation is a simple one-click procedure: just <strong>click on the &#8220;Update Now&#8221; button</strong> when you are notified that a new version is available.', NULL, 'LINE', NULL, 0, '', 'a2c5ffc4b69c8d049bce519b8013c8cb'),
(2521, 'en', 'default', '72247007ca8dd9d5b747cd2d13855ae4', 'In most cases, WordPress will automatically apply maintenance and security updates in the background for you.', NULL, 'LINE', NULL, 0, '', 'f608b0ae3b2099cd13950879df8872a3'),
(2522, 'en', 'default', '83898cec1dbe4f4586c8347c1ba46120', '<strong>Themes and Plugins</strong> &mdash; To update individual themes or plugins from this screen, use the checkboxes to make your selection, then <strong>click on the appropriate &#8220;Update&#8221; button</strong>. To update all of your themes or plugins at once, you can check the box at the top of the section to select all before clicking the update button.', NULL, 'LINE', NULL, 0, '', '98ac0766c2d6280e0f1ccde117bde100'),
(2523, 'en', 'default', 'da5abbe81c1b1941c214b1b4298822cb', '<strong>Translations</strong> &mdash; The files translating WordPress into your language are updated for you whenever any other updates occur. But if these files are out of date, you can <strong>click the &#8220;Update Translations&#8221;</strong> button.', NULL, 'LINE', NULL, 0, '', 'd5ff3112c75a90a53ba4a28d043eb4a7'),
(2524, 'en', 'default', 'c6701576b0781a015b3e4de713ec1078', 'How to Update', NULL, 'LINE', NULL, 0, '', 'a9e00d6f73298647183d6dad93001f27'),
(2525, 'en', 'default', '3ca7a638d575621b115fdfc88b3b002b', '<a href="https://codex.wordpress.org/Dashboard_Updates_Screen">Documentation on Updating WordPress</a>', NULL, 'LINE', NULL, 0, '', 'acea509fdbe89d522e67669f655eddf9'),
(2526, 'en', 'default', 'b9f0488fd6301cdc26b8088675bd89cb', 'Last checked on %1$s at %2$s.', NULL, 'LINE', NULL, 0, '', '7f17ae38b21b0110353ff13011ece282'),
